import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import Header from "./components/admin/header";
import Nav from "./components/admin/nav";
import AddFormProduct from "./pages/admin/product/add";
import List from "./pages/admin/product";
import DashBoard from "./pages/admin/dashboard";
import EditFormProduct from "./pages/admin/product/edit";

import Category from "./pages/admin/category";
import AddFormCategory from "./pages/admin/category/add";
import EditFormCategory from "./pages/admin/category/edit";

import AdminLayout from "./layouts/adminLayout";
import WebsiteLayout from "./layouts/websiteLayout";
import Signup from "./pages/website/signup";
import Signin from "./pages/website/signin";

const Routes = (props) => {
  return (
      <Router>
        
                <Switch>
                  <Route exact path="/admin/:path?">
                    <AdminLayout>
                      <Route exact path="/admin">
                        <DashBoard />
                      </Route>
                      <Route exact path="/admin/product">
                        <List {...props} />
                      </Route>
                      <Route exact path="/admin/product/add">
                        <AddFormProduct {...props} />
                      </Route>
                      <Route exact path="/admin/product/:id/edit">
                        <EditFormProduct {...props} />
                      </Route>

                      <Route exact path="/admin">
                        <DashBoard />
                      </Route>
                      <Route exact path="/admin/category">
                        <Category {...props} />
                      </Route>
                      <Route exact path="/admin/category/add">
                        <AddFormCategory {...props} />
                      </Route>
                      <Route exact path="/admin/category/:id/edit">
                        <EditFormCategory {...props} />
                      </Route>
                    </AdminLayout>
                  </Route>
                </Switch>
                {/* <Switch>
                  <Route exact path="/admin/:path?">
                    <AdminLayout>
                      <Route exact path="/admin">
                        <DashBoard />
                      </Route>
                      <Route exact path="/admin/category">
                        <Category {...props} />
                      </Route>
                      <Route exact path="/admin/category/add">
                        <AddFormCategory {...props} />
                      </Route>
                      <Route exact path="/admin/category/:id/edit">
                        <EditFormCategory {...props} />
                      </Route>
                    </AdminLayout>
                  </Route>
                </Switch> */}
            
      </Router>
  );
};
export default Routes;

// Tạo component và router
