import React, { useState, useEffect } from 'react'
import { useForm } from "react-hook-form";
import { useHistory, useParams, Link } from 'react-router-dom';

import { Editor } from '@tinymce/tinymce-react';

const EditCategory = ({ categories, onEditCate }) => {
    useEffect(() => {
        window.scrollTo(0, 0);
        document.title = "Edit Category";
    }, []);

    const { id } = useParams();

    // Lấy ra danh mục sửa
    const cate = categories.find(cate => cate.id === id);

    let history = useHistory();
    const { handleSubmit, register, errors } = useForm();
    const [content, setContent] = useState(cate.short_desc);

    const handleEditorChange = (e) => {
        setContent(e);
    }

    

    return (
        <div>
            <div className="card shadow mb-4">
                <div className="card-header py-3">
                    <div className="row">
                        <div className="col-sm-12 col-md-6"><h6 className="m-0 font-weight-bold text-primary">Edit Category</h6></div>
                        <div className="col-sm-12 col-md-6">
                        </div>
                    </div>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="form-group">
                            <label htmlFor="">Name</label>
                            <input type="text" name="name"
                                ref={register({
                                    required: "Vui lòng nhập tên danh mục !",
                                    minLength: 5,
                                    pattern: {
                                        value: /^[a-zA-Z0-9_-]/,
                                        message: "Vui lòng bắt đầu bằng 1 số hoặc 1 chữ !"
                                    }
                                })}
                                className="form-control" defaultValue={cate.name} aria-describedby="emailHelp" />
                            {errors.name && errors.name.type === "pattern" && <span className="text-danger">{errors.name.message}</span>}
                            {errors.name && errors.name.type === "required" && <span className="text-danger">{errors.name.message}</span>}
                            {errors.name && errors.name.type === "minLength" && <span className="text-danger">Vui lòng nhập tối thiểu 5 ký tự !</span>}
                        </div>
                        <div className="form-group">
                            <label htmlFor="">Image</label>
                            <div id="image" className="form-group">
                                <img src={cate.image} height={150} className="mb-2" />
                            </div>
                            <div className="custom-file">
                                <input type="file" name="image" className="custom-file-input" id="customFile"
                                    ref={register({
                                        pattern: {
                                            value: /\.(jpe?g|png)$/i,
                                            message: "Không đúng định dạng ảnh !"
                                        }
                                    })}
                                />
                                <label className="custom-file-label" htmlFor="customFile">Choose file</label>
                            </div>
                            {errors.image && <span className="text-danger">{errors.image.message}</span>}
                        </div>
                        <div className="form-group">
                            <label htmlFor="">Mô tả</label>
                            <Editor
                                apiKey='fjpwmpgrpb40v3d3lge46rdgeitrz2o4wevcy44j0pfhngo4'
                                init={{ height: 300 }}
                                value={content}
                                onEditorChange={handleEditorChange}
                            />
                        </div>
                        <div className="form-check">
                            <input type="radio" name="show_menu" ref={register({})} defaultValue={1} defaultChecked={cate.show_menu === "1"} className="form-check-input" />
                            <label className="form-check-label" htmlFor="">Hiển thị</label>
                        </div>
                        <div className="form-check">
                            <input type="radio" name="show_menu" ref={register({})} defaultValue={0} defaultChecked={cate.show_menu === "0"} className="form-check-input" />
                            <label className="form-check-label" htmlFor="">Không hiển thị</label>
                        </div>
                        <br />

                        <Link to="/admin/category"><button type="submit" className="btn btn-danger mr-2">Cancel</button></Link>
                        <button type="submit" className="btn btn-success">Save</button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default EditCategory
