import axiosClient from "./axiosClient";

export const getAll = () => {
  const url = `/categories`;
  return axiosClient.get(url);
};

export const getProductByCateId = (id) => {
  const url = `/categories/${id}/products`;
  return axiosClient.get(url);
};
