import React, { useEffect, useState } from 'react'
import { useForm } from "react-hook-form";
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2'
import Pagination from "react-js-pagination";

const Products = ({ products, categories, onRemove}) => {
    useEffect(() => {
        window.scrollTo(0, 0);
        document.title = "Product List";
    }, []);
    

    // Xóa sản phẩm
    const onHandleClick = (id) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "Deleted data will not be recoverable!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.value) {
                onRemove(id)
                Swal.fire(
                    'Deleted!',
                    'Your file has been deleted.',
                    'success'
                )
            }
        })
    };

    
    // phân trang
    const [currentPage, setCurrentPage] = useState(1);
    const [catePerPage, setCatePerPage] = useState(5);

    const indexLastOfProduct = currentPage * catePerPage;
    const indexFirstOfProduct = indexLastOfProduct - catePerPage;
    const currentProduct = products.slice(indexFirstOfProduct, indexLastOfProduct);

    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    }

    return (
        <div className="card shadow mb-4">
            <div className="card-header py-3">
                <div className="row">
                    <div className="col-sm-12 col-md-6"><h6 className="m-0 font-weight-bold text-primary">Product list</h6></div>
                </div>
            </div>

            <div className="card-body">
                <div className="table-responsive">
                    <table className="table">
                        <thead className="thead-light">
                            <tr>
                                <th scope="col">#</th>
                                <th scope="col">Name</th>
                                <th scope="col">Image</th>
                                <th scope="col">Price</th>
                                <th scope="col">Sale Price</th>
                                <th scope="col">Category</th>
                                <th scope="col">Status</th>
                                <th scope="col">
                                    <Link to="/admin/product/add"><button className="btn btn-primary"><i className="fas fa-plus" /> Add</button></Link>
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {currentProduct.map((product, index) => {
                                return <tr key={index}>
                                    <th scope="row">{index + 1}</th>
                                    <td><Link to={`/product/${product.id}`}>{product.name}</Link></td>
                                    <td><img src={product.image} width='150' /></td>
                                    <td>${product.price}</td>
                                    <td>${product.sale_price}</td>
                                    {/* <td>{categories.map(cate => cate.id === product.cate_id ? cate.name : "")}</td> */}
                                    <td>{product.status === "1" ? "In stock" : "Out stock"}</td>
                                    <td>
                                        <button className="btn btn-danger btn-circle mr-2" onClick={() => { onHandleClick(product.id) }}> <i className="fas fa-trash"></i></button>
                                        <Link to={`/admin/product/edit/${product.id}`}><button className="btn btn-success btn-circle"><i className="fas fa-edit"></i></button></Link>
                                    </td>
                                </tr>
                            })}
                        </tbody>
                    </table>
                    <div className="pagination pagination-sm justify-content-center">
                        <Pagination
                            activePage={currentPage}
                            itemsCountPerPage={5}
                            totalItemsCount={products.length}
                            pageRangeDisplayed={100}
                            onChange={handlePageChange}
                            itemClass="page-item"
                            linkClass="page-link"
                        />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Products
