import { useEffect, useState } from "react";
import apiProducts from "./api/productAPI";
import apiCategories from './api/categoryApi';
import "./styles.css";
import Routes from "./routes";

export default function App() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);


  //---------------------product----------------------------------------

  useEffect(() => {
    // call api
    const getProducts = async () => {
      try {
        const { data } = await apiProducts.getAll();
        setProducts(data);
      } catch (error) {
        console.log("error", error);
      }
    };
    getProducts();
  }, []);
  // Xóa sản phẩm
  const removeProduct = async (id) => {
    try {
      await apiProducts.remove(id);
      const newProducts = products.filter((item) => item.id !== id);
      setProducts(newProducts);
    } catch (error) {
      console.log(error);
    }
  };
  // Thêm sản phẩm
  const addProduct = async (item) => {
    try {
      const { data } = await apiProducts.add(item); // tra ve 1 object { data: ..., status: ...}
      setProducts([...products, data]);
    } catch (error) {
      console.log(error);
    }
  }; // item la 1 {}
  const editProduct = async (item) => {
    try {
      const { data } = await apiProducts.edit(item);
      const newProducts = products.map((product) =>
        product.id == item.id ? data : product
      );
      setProducts(newProducts);
    } catch (error) {}
  };
  // ví dụ: promise


//------------------------------------------------------------------------------------------

  //-------------------------category-----------------------------------------
  useEffect(() => {
    // call api
    const getCategories = async () => {
      try {
        const { data } = await apiCategories.getAll();
        setCategories(data);
      } catch (error) {
        console.log("error", error);
      }
    };
    getCategories();
  }, []);
  // Xóa sản phẩm
  const removeCate = async (id) => {
    try {
      await apiCategories.remove(id);
      const newCategories = categories.filter((item) => item.id !== id);
      setCategories(newCategories);
    } catch (error) {
      console.log(error);
    }
  };
  // Thêm sản phẩm
  const addCate = async (item) => {
    try {
      const { data } = await apiCategories.add(item); // tra ve 1 object { data: ..., status: ...}
      setCategories([...categories, data]);
    } catch (error) {
      console.log(error);
    }
  }; // item la 1 {}
  const editCate = async (item) => {
    try {
      const { data } = await apiCategories.edit(item);
      const newCategories = categories.map((category) =>
      category.id == item.id ? data : category
      );
      setCategories(newCategories);
    } catch (error) {}
  };
  return (
    <Routes
      categories={categories}
      products={products}
      onRemove={removeProduct}
      onAdd={addProduct}
      onEdit={editProduct}
      onRemoveCate={removeCate}
      onAddCate={addCate}
      onEditCate={editCate}
    />
  );
}
